const login = async (req, res) => {
  let {
    username,
    pwd
  } = req.body;
  if (!username || !pwd || !username) {
    //!isNumber(username)
    return res.status(200).json({
      message: "ERROR!!!"
    });
  }
  try {
    const [rows] = await connection.query("SELECT * FROM users WHERE phone = ? AND password = ? ", [username, md5(pwd)]);
    if (rows.length == 1) {
      if (rows[0].status == 1) {
        const {
          password,
          money,
          ip,
          veri,
          ip_address,
          status,
          time,
          ...others
        } = rows[0];
        const accessToken = jwt.sign({
          user: {
            ...others
          },
          timeNow: timeNow
        }, process.env.JWT_ACCESS_TOKEN, {
          expiresIn: "1d"
        });
        await connection.execute("UPDATE `users` SET `token` = ? WHERE `phone` = ? ", [md5(accessToken), username]);
        return res.status(200).json({
          message: "Login Successfully!",
          status: true,
          token: accessToken,
          value: md5(accessToken)
        });
      } else {
        return res.status(200).json({
          message: "Account has been locked",
          status: false
        });
      }
    } else {
      return res.status(200).json({
        message: "Incorrect Username or Password",
        status: false
      });
    }
  } catch (error) {
    if (error) console.log(error);
  }
};
