   // Total level-4 referrals
   let f4 = 0
   for (let i = 0; i < f1s.length; i++) {
      const f1_code = f1s[i].code
      const [f2s] = await connection.query("SELECT `phone`, `code`,`invite` FROM users WHERE `invite` = ? ", [f1_code])
