         status: false,
         timeStamp: timeNow,
      })
   }
   const [redenvelopes] = await connection.query("SELECT * FROM redenvelopes WHERE id_redenvelope = ?", [code]);

   if (redenvelopes.length == 0) {
      return res.status(200).json({
         message: "Redemption code error",
         status: false,
         timeStamp: timeNow,
      })
   } 

   const [used_code] = await connection.query('SELECT * from redenvelopes_used WHERE phone_used = ? AND id_redenvelops = ?',[userInfo.phone,code]);

   if(used_code[0]){
      return res.status(200).json({
         message: "You have already used this redemption code",
         status: false,
         timeStamp: timeNow,
      })
   }

   else {
      let infoRe = redenvelopes[0]
      const d = new Date()
      const time = d.getTime()
      if (infoRe.status == 0) {
         if (infoRe.used === 1) {
            await connection.query("UPDATE redenvelopes SET used = ?, status = ? WHERE `id_redenvelope` = ? ", [0, 1, infoRe.id_redenvelope])
         } else {
            await connection.query("UPDATE redenvelopes SET used = ?, status = ? WHERE `id_redenvelope` = ? ", [infoRe.used - 1, 0, infoRe.id_redenvelope])
         }
         await connection.query("UPDATE users SET money = money + ? WHERE `phone` = ? ", [infoRe.money, userInfo.phone])
         let sql = "INSERT INTO redenvelopes_used SET phone = ?, phone_used = ?, id_redenvelops = ?, money = ?, `time` = ? "
         await connection.query(sql, [infoRe.phone, userInfo.phone, infoRe.id_redenvelope, infoRe.money, time])
         return res.status(200).json({
            message: `Received successfully +${infoRe.money}`,
            status: true,
            timeStamp: timeNow,
         })
      } else {
         return res.status(200).json({
            message: "Gift code expired",
            status: false,
            timeStamp: timeNow,
         })
      }
   }
}

