const partnerData = async (req, res) => {
   const now = new Date();
   const tenDaysAgo = new Date(now.getTime() - (10 * 24 * 60 * 60 * 1000));

   const startOfDay = new Date(tenDaysAgo.setHours(0, 0, 0, 0)).getTime();
   const endOfDay = now.getTime();

   const timeNow = now.toISOString();
   const auth = req.cookies.auth;

   const [user] = await connection.execute('SELECT code FROM users WHERE token = ?', [auth]);

   if (!user[0]) {
      return res.status(200).json({
         message: "FAILED",
         status: false,
         timeStamp: timeNow,
      });
   }

   const [members] = await connection.execute(
      `SELECT phone, time 
       FROM users 
       WHERE invite = ? 
       AND CAST(time AS UNSIGNED) BETWEEN ? AND ?`,
      [user[0].code, startOfDay, endOfDay]
   );

   if (members.length === 0) {
      return res.status(200).json({
         message: "No members found",
         status: false,
         timeStamp: timeNow,
      });
   }

   let data = [];

   await Promise.all(members.map(async (val) => {
      const [recharge] = await connection.execute(
         'SELECT * FROM recharge WHERE phone = ? ORDER BY CAST(time AS UNSIGNED) ASC', 
         [val.phone]
      );

      let firstRecharge = recharge[0]?.money || 0;
      let secondRecharge = recharge[1]?.money || 0;
      let thirdRecharge = recharge[2]?.money || 0;
      let totalRecharge = recharge.reduce((acc, val) => acc + val.money, 0);
      let phone = val.phone;

      const date = new Date(parseInt(val.time));
      const time = date.toLocaleString();

      data.push({ firstRecharge, secondRecharge, thirdRecharge, totalRecharge, phone, time });
   }));

   return res.status(200).json({
      message: "success",
      status: true,
      timeStamp: timeNow,
      data: data
   });
};



