const recharge = async (req, res) => {
  let auth = req.cookies.auth;
  if (!auth) {
    return res.status(200).json({
      message: "Failed",
      status: false,
      timeStamp: timeNow
    });
  }
  const limit = req.query.limit ? parseInt(req.query.limit) : 10;
  const page = req.query.page ? parseInt(req.query.page) : 1;
  const offset = (page - 1) * limit;
  const [recharge] = await connection.query(`SELECT * FROM recharge WHERE status = 0 ORDER BY id DESC LIMIT ? OFFSET ?`, [limit, offset]);
  const [rechargeTotalCount] = await connection.query(`SELECT COUNT(*) as totalCount FROM recharge WHERE status = 0`);
  const [recharge2] = await connection.query(`SELECT * FROM recharge WHERE status != 0 ORDER BY id DESC LIMIT ? OFFSET ?`, [limit, offset]);
  const [recharge2TotalCount] = await connection.query(`SELECT COUNT(*) as totalCount FROM recharge WHERE status != 0`);
  const [withdraw] = await connection.query(`SELECT * FROM withdraw WHERE status = 0 ORDER BY id DESC LIMIT ? OFFSET ?`, [limit, offset]);
  const [withdrawTotalCount] = await connection.query(`SELECT COUNT(*) as totalCount FROM withdraw WHERE status = 0`);
  const [withdraw2] = await connection.query(`SELECT * FROM withdraw WHERE status != 0 ORDER BY id DESC LIMIT ? OFFSET ?`, [limit, offset]);
  const [withdraw2TotalCount] = await connection.query(`SELECT COUNT(*) as totalCount FROM withdraw WHERE status != 0`);
  return res.status(200).json({
    message: "Success",
    status: true,
    datas: recharge,
    datas2: recharge2,
    datas3: withdraw,
    datas4: withdraw2,
    count: [rechargeTotalCount, recharge2TotalCount, withdrawTotalCount, withdraw2TotalCount]
  });
};
