      let ampm = date.getHours() < 12 ? "AM" : "PM"

      let minutes = formateT(date.getMinutes())
      let seconds = formateT(date.getSeconds())

      return years + "-" + months + "-" + days + " " + hours + ":" + minutes + ":" + seconds + " " + ampm
