function makeid(length) {
    var result = '';
    var characters = '0123456789';
    var charactersLength = characters.length;
    for (var i = 0; i < length; i++) {
        result += characters.charAt(Math.floor(Math.random() * charactersLength));
    }
    return result;
}

const add5D = async(game) => {
    try {
        let join = '';
        if (game == 1) join = 'k5d'; 
        if (game == 3) join = 'k5d3';
        if (game == 5) join = 'k5d5';
        if (game == 10) join = 'k5d10';

        let result2 = makeid(5);
        let timeNow = Date.now();
        let [k5D] = await connection.query(`SELECT period FROM 5d WHERE status = 0 AND game = ${game} ORDER BY id DESC LIMIT 1 `);
        const [setting] = await connection.query('SELECT * FROM `admin` ');
        let period = k5D[0].period;

        let nextResult = '';
        if (game == 1) nextResult = setting[0].k5d;
        if (game == 3) nextResult = setting[0].k5d3;
        if (game == 5) nextResult = setting[0].k5d5;
        if (game == 10) nextResult = setting[0].k5d10;

        // console.log(setting[0],'admin setting');

        const [waiting] = await connection.query(
            `SELECT * FROM result_5d WHERE status = 0 AND level = 0 AND game = '${game}' ORDER BY id ASC `
          );

        //   console.log(waiting,'current bets');

        let newArr = '';
        if (nextResult == '-1' && !waiting[1]) {
            await connection.execute(`UPDATE 5d SET result = ?,status = ? WHERE period = ? AND game = "${game}"`, [result2, 1, period]);
            newArr = '-1';
        }

        else if(nextResult == '-1' && waiting[1]){
            function calculatePayout(waiting, winningString) {
                let totalPayout = 0;
                let results = winningString.split('').map(Number); // Convert the string to an array of numbers

                // Calculate the sum for 'total' category
                const totalSum = results.reduce((acc, val) => acc + val, 0);

                for (let bet of waiting) {
                    let payout = 0;
                    let betValue = bet.money * bet.amount;
                    let index = bet.join_bet.charCodeAt(0) - 'a'.charCodeAt(0); // index 0 for 'a', 1 for 'b', etc.

                    if (bet.join_bet === 'total') {
                        if (bet.bet === 'c' && totalSum % 2 === 0) payout = betValue * 2;
                        else if (bet.bet === 'l' && totalSum % 2 !== 0) payout = betValue * 2;
                        else if (bet.bet === 'b' && totalSum > 22) payout = betValue * 2;
                        else if (bet.bet === 's' && totalSum <= 22) payout = betValue * 2;
                    } else {
                        let result = results[index];
                        if (bet.bet === String(result)) payout = betValue * 9;
                        else if (bet.bet === 'c' && result % 2 === 0) payout = betValue * 2;
                        else if (bet.bet === 'l' && result % 2 !== 0) payout = betValue * 2;
                        else if (bet.bet === 'b' && result > 4) payout = betValue * 2;
                        else if (bet.bet === 's' && result <= 4) payout = betValue * 2;
                    }

                    totalPayout += payout;
                }

                return totalPayout;
            }

            function findOptimalWinningString(waiting) {
                const totalReceivedBets = waiting.reduce((acc, bet) => acc + (bet.money * bet.amount), 0);
                const minRequiredProfit = totalReceivedBets * 0.25; // 25% of total received bets

                let smallestPayoutOptions = [];
                let minPayout = Infinity;
                let zeroPayoutOptions = [];

                for (let i = 0; i <= 99999; i++) {
                    let winningString = String(i).padStart(5, '0'); // Generate the 5-digit string
                    let payout = calculatePayout(waiting, winningString);
                    let profit = totalReceivedBets - payout;

                    if (payout === 0) {
                        zeroPayoutOptions.push(winningString);
                    } else if (profit >= minRequiredProfit) {
                        if (payout < minPayout) {
                            minPayout = payout;
                            smallestPayoutOptions = [winningString]; // Start a new list with this option
                        } else if (payout === minPayout) {
                            smallestPayoutOptions.push(winningString); // Add to the list of smallest payout options
                        }
                    }
                }

                if (smallestPayoutOptions.length > 0) {
                    // Randomly select from smallest payout options
                    return smallestPayoutOptions[Math.floor(Math.random() * smallestPayoutOptions.length)];
                } else {
                    // If no valid smallest payout option, randomly select from zero payout options
                    return zeroPayoutOptions[Math.floor(Math.random() * zeroPayoutOptions.length)];
                }
            }

            const optimalWinningString = findOptimalWinningString(waiting);
            console.log("Optimal Winning String:", optimalWinningString);
            await connection.execute(`UPDATE 5d SET result = ?,status = ? WHERE period = ? AND game = ${game}`, [optimalWinningString, 1, period]);

        }

        else {
            let result = '';
            let arr = nextResult.split('|');
            let check = arr.length;
            if (check == 1) {
                newArr = '-1';
            } else {
                for (let i = 1; i < arr.length; i++) {
                    newArr += arr[i] + '|';
                }
                newArr = newArr.slice(0, -1);
            }
            result = arr[0];
            await connection.execute(`UPDATE 5d SET result = ?,status = ? WHERE period = ? AND game = ${game}`, [result, 1, period]);
        }
        const sql = `INSERT INTO 5d SET period = ?, result = ?, game = ?, status = ?, time = ?`;
        await connection.execute(sql, [Number(period) + 1, 0, game, 0, timeNow]);

        if (game == 1) join = 'k5d';
        if (game == 3) join = 'k5d3';
        if (game == 5) join = 'k5d5';
        if (game == 10) join = 'k5d10'; 

        await connection.execute(`UPDATE admin SET ${join} = ?`, [newArr]);
    } catch (error) {
        if (error) {
            console.log(error);
        }
    }
}

