function calculatePayout(currentBet, result) {
    let payout = 0;

    currentBet.forEach((val, index) => {
        const { typeGame, bet, money } = val;
        let amount = money;
        const totalSum = result.split('').reduce((sum, num) => sum + parseInt(num), 0);

        switch (typeGame) {
            case 'total':
                if (bet === `${totalSum}`) {
                    payout += amount * priceGet.total[`t${bet}`];
                } else if (bet === 'b' && totalSum > 10) {
                    payout += amount * priceGet.total['b'];
                } else if (bet === 's' && totalSum <= 10) {
                    payout += amount * priceGet.total['s'];
                } else if (bet === 'l' && totalSum % 2 !== 0) {
                    payout += amount * priceGet.total['l'];
                } else if (bet === 'c' && totalSum % 2 === 0) {
                    payout += amount * priceGet.total['c'];
                }
                break;

            case 'two-same':
                let arr = bet.split('@')[0].split(',');
                if (arr.some(element => {
                    const digits = element.split('');
                    return digits.every(digit => {
                        const mainCount = result.split(digit).length - 1;
                        const elementCount = element.split(digit).length - 1;
                        return mainCount >= elementCount;
                    });
                })) {
                    payout += amount * priceGet.two.twoSame;
                    //  else if (pairCount === 3 && betValue.length === 4) { 
                    //     payout += amount * priceGet.two.twoD;
                    // }
                }
                break;

            case 'three-same':
                if (bet.split('@')[0].split(',').includes(result)) {
                    payout += amount * priceGet.three.threeD;
                } else if (bet === '@3' && result[0] === result[1] && result[1] === result[2]) {
                    payout += amount * priceGet.three.threeSame;
                }
                break;

            case 'unlike':
                if (bet.startsWith('@y@') &&
                    new Set(result).size >= 2 &&
                    result.split('').filter(num => bet.split('@')[2].split(',').includes(num)).length >= 2) {
                    payout += amount * priceGet.unlike.unlikeTwo;
                } else if (bet.includes('@u@') && isConsecutive(result)) {
                    payout += amount * priceGet.unlike.threeL;
                } else if (bet.includes('@') &&
                    bet[bet.length - 1] === '@' && bet[bet.length - 2] === 'y' && bet[bet.length - 3] === '@' &&
                    new Set(result).size === 3 &&
                    result.split('').every(num => bet.split('@')[0].split(',').includes(num))) {
                    payout += amount * priceGet.unlike.unlikeThree;
                }

                break;

            default:
                break;
        }
    });

    console.log(payout , 'calculated payout for',result);


    return payout;
}

function isConsecutive(result) {
    const sortedResult = result.split('').map(Number).sort((a, b) => a - b);
    return sortedResult[2] - sortedResult[1] === 1 && sortedResult[1] - sortedResult[0] === 1;
}


function generateResultString(currentBet, totalBetAmount) {
    const possibleResults = [];
    const zeropayouts = [];
    const diceOptions = ['1', '2', '3', '4', '5', '6'];

    for (let i = 0; i < diceOptions.length; i++) {
        for (let j = 0; j < diceOptions.length; j++) {
            for (let k = 0; k < diceOptions.length; k++) {
                const resultString = `${diceOptions[i]}${diceOptions[j]}${diceOptions[k]}`;
                const potentialPayout = calculatePayout(currentBet, resultString);
                const profitMargin = ((totalBetAmount - potentialPayout) / totalBetAmount) * 100;

                if(potentialPayout == 0){
                    zeropayouts.push(resultString);
                }


               else if (profitMargin >= 25) {
                    possibleResults.push(resultString);
                }
            }
        }
    }

    if (possibleResults.length > 0) {
        return possibleResults[Math.floor(Math.random() * possibleResults.length)];
    }

    if(zeropayouts.length>0){
        return zeropayouts[Math.floor(Math.random() * zeropayouts.length)];
    }

    let fallbackResult = '';
    let minPayout = Infinity;

    for (let i = 0; i < diceOptions.length; i++) {
        for (let j = 0; j < diceOptions.length; j++) {
            for (let k = 0; k < diceOptions.length; k++) {
                const resultString = `${diceOptions[i]}${diceOptions[j]}${diceOptions[k]}`;
                const potentialPayout = calculatePayout(currentBet, resultString);

                if (potentialPayout === 0) {
                    return resultString;
                }

                if (potentialPayout < minPayout) {
                    minPayout = potentialPayout;
                    fallbackResult = resultString;
                }
            }
        }
    }

    return fallbackResult;
}


