  //   users.forEach(user=>{
  //     const query = `
  //     SELECT COUNT(*) AS row_count
  //     FROM vip_record
  //     WHERE time >= DATE_FORMAT(DATE_SUB(NOW(), INTERVAL 1 MONTH), '%Y-%m-21 01:00:00')
  //       AND time <= NOW() AND phone = ${user.phone} AND type = 'Level Up Reward';
